import React from 'react';

const ResultDisplay = ({ result }) => {
  if (!result) return null;

  const { prediction, probability, model_type } = result;
  const isPCOS = prediction === 1 || prediction === true;
  const probabilityPercentage = typeof probability === 'number' 
    ? Math.round(probability * 100) 
    : 'N/A';

  // Determine risk level based on probability
  let riskLevel = 'Unknown';
  let riskColor = 'gray';
  
  if (typeof probability === 'number') {
    if (probability >= 0.75) {
      riskLevel = 'High';
      riskColor = 'red';
    } else if (probability >= 0.5) {
      riskLevel = 'Moderate';
      riskColor = 'orange';
    } else if (probability >= 0.25) {
      riskLevel = 'Low';
      riskColor = 'yellow';
    } else {
      riskLevel = 'Very Low';
      riskColor = 'green';
    }
  }

  // Check if fallback model was used
  const isFallbackModel = model_type === 'fallback';

  return (
    <div className="mt-8 bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-4 text-gray-800">Analysis Results</h2>
      
      {isFallbackModel && (
        <div className="mb-4 pb-4 border-b border-gray-200">
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
            Using Fallback Model
          </span>
          <p className="text-sm text-yellow-700 mt-2">
            A simplified rule-based model was used for this prediction as the trained ML model was not available.
          </p>
        </div>
      )}
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <h3 className="text-lg font-medium text-gray-700 mb-2">Prediction</h3>
          <div className={`px-4 py-3 rounded-lg ${isPCOS ? 'bg-red-50' : 'bg-green-50'}`}>
            <p className={`text-lg font-bold ${isPCOS ? 'text-red-700' : 'text-green-700'}`}>
              {isPCOS ? 'PCOS/PCOD Detected' : 'No PCOS/PCOD Detected'}
            </p>
            <p className="text-sm mt-1 text-gray-600">
              This assessment is based on the provided information and should be confirmed by healthcare professionals.
            </p>
          </div>
        </div>
        
        <div>
          <h3 className="text-lg font-medium text-gray-700 mb-2">Risk Assessment</h3>
          <div className="px-4 py-3 rounded-lg bg-gray-50">
            <div className="flex items-center">
              <div className="w-full bg-gray-200 rounded-full h-2.5">
                <div 
                  className={`h-2.5 rounded-full bg-${riskColor}-500`} 
                  style={{ width: `${probabilityPercentage}%` }}
                ></div>
              </div>
              <span className="ml-3 text-sm font-medium">{probabilityPercentage}%</span>
            </div>
            <p className={`text-sm mt-2 font-semibold text-${riskColor}-700`}>
              {riskLevel} Risk Level
            </p>
          </div>
        </div>
      </div>
      
      <div className="mt-6 bg-gray-50 p-4 rounded-lg text-sm text-gray-500">
        <p className="font-medium mb-1">Important Notice:</p>
        <p>
          This tool provides an estimation based on the information provided and is not a definitive diagnosis.
          {isFallbackModel 
            ? ' This result was generated using a simplified rule-based model, not the full machine learning model.' 
            : ' Machine learning models have limitations and may not capture all aspects of your health condition.'}
        </p>
        <p className="mt-2">
          Always consult with a healthcare provider for proper diagnosis and treatment. If you are experiencing 
          symptoms, please seek medical attention promptly.
        </p>
      </div>
    </div>
  );
};

export default ResultDisplay;
