import React from 'react';

const ResultDisplay = ({ result }) => {
  if (!result) return null;

  const { pcos_detected, probability } = result;
  
  return (
    <div className={`mt-6 p-6 rounded-lg ${pcos_detected ? 'bg-red-50' : 'bg-green-50'}`}>
      <div className="flex items-center">
        <div className={`flex-shrink-0 w-12 h-12 rounded-full flex items-center justify-center ${pcos_detected ? 'bg-red-100' : 'bg-green-100'}`}>
          {pcos_detected ? (
            <svg className="w-6 h-6 text-red-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
            </svg>
          ) : (
            <svg className="w-6 h-6 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
            </svg>
          )}
        </div>
        <div className="ml-4">
          <h3 className={`text-xl font-semibold ${pcos_detected ? 'text-red-800' : 'text-green-800'}`}>
            {pcos_detected ? 'PCOS/PCOD Detected' : 'No PCOS/PCOD Detected'}
          </h3>
          <p className="mt-1 text-sm text-gray-600">
            Prediction confidence: {(probability * 100).toFixed(2)}%
          </p>
        </div>
      </div>
      
      <div className="mt-4">
        <div className="w-full bg-gray-200 rounded-full h-2.5">
          <div 
            className={`h-2.5 rounded-full ${pcos_detected ? 'bg-red-600' : 'bg-green-600'}`} 
            style={{ width: `${probability * 100}%` }}
          ></div>
        </div>
      </div>
      
      {pcos_detected && (
        <div className="mt-4 p-4 bg-white rounded border border-red-200">
          <h4 className="text-md font-medium text-gray-800 mb-2">Important Notice:</h4>
          <p className="text-sm text-gray-600">
            This is only a preliminary screening result based on machine learning analysis. 
            Please consult with a healthcare professional for a proper diagnosis and treatment plan.
          </p>
        </div>
      )}
      
      {!pcos_detected && (
        <div className="mt-4 p-4 bg-white rounded border border-green-200">
          <h4 className="text-md font-medium text-gray-800 mb-2">Health Recommendation:</h4>
          <p className="text-sm text-gray-600">
            While the analysis doesn't indicate PCOS/PCOD, it's recommended to maintain regular health check-ups
            and consult with healthcare professionals for any persistent symptoms.
          </p>
        </div>
      )}
    </div>
  );
};

export default ResultDisplay;
