import React, { useState, useEffect } from 'react';
import ClinicalForm from './components/ClinicalForm';
import ImageUpload from './components/ImageUpload';
import ResultDisplay from './components/ResultDisplay';
import { predictWithMLP, predictWithCNN, predictWithFusion, checkServerHealth, checkModelStatus } from './api';
import { PulseLoader } from 'react-spinners';

const App = () => {
  // State for application
  const [selectedModel, setSelectedModel] = useState('mlp'); // Default to MLP model
  const [isLoading, setIsLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const [serverStatus, setServerStatus] = useState('checking');
  const [imageData, setImageData] = useState(null);
  const [modelStatus, setModelStatus] = useState({
    mlp_model: { file_exists: false, pytorch_available: false, fallback_available: false },
    cnn_model: { file_exists: false, pytorch_available: false, fallback_available: false },
    fusion_model: { file_exists: false, pytorch_available: false, fallback_available: false }
  });

  // Check server status and model status on mount
  useEffect(() => {
    const checkServer = async () => {
      try {
        const isHealthy = await checkServerHealth();
        setServerStatus(isHealthy ? 'online' : 'offline');
        
        if (isHealthy) {
          // Also check model status
          const status = await checkModelStatus();
          setModelStatus(status);
        }
      } catch (error) {
        setServerStatus('offline');
      }
    };
    
    checkServer();
  }, []);

  // Check if a model is available (either as PyTorch model or fallback)
  const isModelAvailable = (modelType) => {
    if (modelType === 'mlp') {
      return modelStatus.mlp_model.file_exists || modelStatus.mlp_model.fallback_available;
    } else if (modelType === 'cnn') {
      return modelStatus.cnn_model.file_exists || modelStatus.cnn_model.fallback_available;
    } else if (modelType === 'fusion') {
      return modelStatus.fusion_model.file_exists || modelStatus.fusion_model.fallback_available;
    }
    return false;
  };

  // Get model status display text
  const getModelStatusText = (modelType) => {
    if (modelType === 'mlp') {
      if (modelStatus.mlp_model.file_exists) return "Available (PyTorch)";
      if (modelStatus.mlp_model.fallback_available) return "Available (Fallback)";
    } else if (modelType === 'cnn') {
      if (modelStatus.cnn_model.file_exists) return "Available (PyTorch)";
      if (modelStatus.cnn_model.fallback_available) return "Available (Fallback)";
    } else if (modelType === 'fusion') {
      if (modelStatus.fusion_model.file_exists) return "Available (PyTorch)";
      if (modelStatus.fusion_model.fallback_available) return "Available (Fallback)";
    }
    return "Not Available";
  };

  // Handle model selection
  const handleModelChange = (e) => {
    setSelectedModel(e.target.value);
    // Reset results when changing models
    setResult(null);
  };

  // Handle clinical form submission
  const handleClinicalSubmit = async (formData) => {
    try {
      setIsLoading(true);
      setError('');
      setResult(null);
      
      // Process based on selected model
      if (selectedModel === 'mlp') {
        const result = await predictWithMLP(formData);
        setResult(result);
      } 
      else if (selectedModel === 'fusion' && imageData) {
        const result = await predictWithFusion(formData, imageData);
        setResult(result);
      }
      else if (selectedModel === 'fusion') {
        setError('Fusion model requires both clinical data and an ultrasound image.');
      }
    } catch (error) {
      setError(`Error: ${error.message || 'Failed to process your request'}`);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle image upload
  const handleImageUpload = async (imageBase64) => {
    try {
      setIsLoading(true);
      setError('');
      setResult(null);
      setImageData(imageBase64);
      
      // Process based on selected model
      if (selectedModel === 'cnn') {
        const result = await predictWithCNN(imageBase64);
        setResult(result);
      }
    } catch (error) {
      setError(`Error: ${error.message || 'Failed to process your request'}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-gray-900">PCOS/PCOD Detection System</h1>
          
          {/* Server status indicator */}
          <div className="mt-2 flex items-center">
            <div className={`h-3 w-3 rounded-full mr-2 ${
              serverStatus === 'online' ? 'bg-green-500' : 
              serverStatus === 'offline' ? 'bg-red-500' : 'bg-yellow-500'
            }`}></div>
            <span className="text-sm text-gray-600">
              Server: {serverStatus === 'checking' ? 'Checking connection...' : serverStatus}
            </span>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        {/* Model Selection */}
        <div className="bg-white p-6 rounded-lg shadow-md mb-6">
          <h2 className="text-xl font-semibold mb-4 text-gray-800">Select Prediction Model</h2>
          
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <label className={`flex items-center p-4 rounded-lg border-2 cursor-pointer transition-colors 
              ${selectedModel === 'mlp' ? 'border-primary-500 bg-primary-50' : 'border-gray-200 hover:bg-gray-50'}
              ${!isModelAvailable('mlp') ? 'opacity-50' : ''}`}>
              <input 
                type="radio" 
                name="model" 
                value="mlp" 
                checked={selectedModel === 'mlp'} 
                onChange={handleModelChange}
                className="h-4 w-4 text-primary-600"
                disabled={!isModelAvailable('mlp')}
              />
              <div className="ml-3">
                <span className="block font-medium text-gray-900">Clinical Features Model</span>
                <span className="block text-sm text-gray-500">Uses only patient clinical data</span>
                <span className={`block text-xs mt-1 ${
                  modelStatus.mlp_model.file_exists ? 'text-green-600' : 
                  modelStatus.mlp_model.fallback_available ? 'text-orange-500' : 'text-red-500'
                }`}>
                  {getModelStatusText('mlp')}
                </span>
              </div>
            </label>

            <label className={`flex items-center p-4 rounded-lg border-2 cursor-pointer transition-colors 
              ${selectedModel === 'cnn' ? 'border-primary-500 bg-primary-50' : 'border-gray-200 hover:bg-gray-50'}
              ${!isModelAvailable('cnn') ? 'opacity-50' : ''}`}>
              <input 
                type="radio" 
                name="model" 
                value="cnn" 
                checked={selectedModel === 'cnn'} 
                onChange={handleModelChange}
                className="h-4 w-4 text-primary-600"
                disabled={!isModelAvailable('cnn')}
              />
              <div className="ml-3">
                <span className="block font-medium text-gray-900">Ultrasound Image Model</span>
                <span className="block text-sm text-gray-500">Uses only ultrasound images</span>
                <span className={`block text-xs mt-1 ${
                  modelStatus.cnn_model.file_exists ? 'text-green-600' : 
                  modelStatus.cnn_model.fallback_available ? 'text-orange-500' : 'text-red-500'
                }`}>
                  {getModelStatusText('cnn')}
                </span>
              </div>
            </label>

            <label className={`flex items-center p-4 rounded-lg border-2 cursor-pointer transition-colors 
              ${selectedModel === 'fusion' ? 'border-primary-500 bg-primary-50' : 'border-gray-200 hover:bg-gray-50'}
              ${!isModelAvailable('fusion') ? 'opacity-50' : ''}`}>
              <input 
                type="radio" 
                name="model" 
                value="fusion" 
                checked={selectedModel === 'fusion'} 
                onChange={handleModelChange}
                className="h-4 w-4 text-primary-600"
                disabled={!isModelAvailable('fusion')}
              />
              <div className="ml-3">
                <span className="block font-medium text-gray-900">Fusion Model</span>
                <span className="block text-sm text-gray-500">Uses both clinical data & images</span>
                <span className={`block text-xs mt-1 ${
                  modelStatus.fusion_model.file_exists ? 'text-green-600' : 
                  modelStatus.fusion_model.fallback_available ? 'text-orange-500' : 'text-red-500'
                }`}>
                  {getModelStatusText('fusion')}
                </span>
              </div>
            </label>
          </div>
        </div>

        {/* Error message */}
        {error && (
          <div className="bg-red-50 border-l-4 border-red-400 p-4 mb-6">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm text-red-700">{error}</p>
              </div>
            </div>
          </div>
        )}

        {/* Fallback model notice */}
        {result && result.model_type === 'fallback' && (
          <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-6">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-yellow-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm text-yellow-700">
                  Note: This prediction was made using a simplified fallback model, as the original trained model was not available.
                  The results are based on general medical knowledge about PCOS/PCOD indicators, not on the trained machine learning model.
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Loading indicator */}
        {isLoading && (
          <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg shadow-xl">
              <PulseLoader color="#4f46e5" size={15} />
              <p className="mt-4 text-gray-600">Processing your request...</p>
            </div>
          </div>
        )}

        {/* Conditional rendering of input forms based on selected model */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {(selectedModel === 'mlp' || selectedModel === 'fusion') && (
            <div className="col-span-1">
              <ClinicalForm onFormSubmit={handleClinicalSubmit} isLoading={isLoading} />
            </div>
          )}
          
          {(selectedModel === 'cnn' || selectedModel === 'fusion') && (
            <div className="col-span-1">
              <ImageUpload onImageUpload={handleImageUpload} isLoading={isLoading} />
            </div>
          )}
        </div>

        {/* Results Display */}
        {result && <ResultDisplay result={result} />}
      </main>

      <footer className="bg-white border-t border-gray-200 py-8 mt-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <p className="text-center text-gray-500 text-sm">
            © {new Date().getFullYear()} PCOS/PCOD Detection System. This is for educational purposes only.
            <br />
            Not a substitute for professional medical advice. Always consult with healthcare professionals.
          </p>
        </div>
      </footer>
    </div>
  );
};

export default App;
